// In App.js
import React from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import Login from './components/Auth/Login';
import Register from './components/Auth/Register';
import Dashboard from './components/Dashboard/Dashboard'; // Import Dashboard component
// import ClassList from './components/Class/ClassList';
// import StudentList from './components/Student/StudentList';

function App() {
  // Check if user is authenticated
  const isAuthenticated = () => {
    const token = localStorage.getItem('token'); // Assuming you store the JWT token in local storage
    return token !== null;
  };

  return (
    <Router>
      <div>
        <Routes>
          <Route exact path="/" element={isAuthenticated() ? <Navigate to="/dashboard" /> : <Login />} />
          <Route path="/register" element={<Register />} />
          <Route path="/dashboard" element={isAuthenticated() ? <Dashboard /> : <Navigate to="/" />} /> {/* Protected route for dashboard */}
          {/* <Route path="/classes" element={< ClassList />} /> */}
          {/* <Route path="/students" element={< StudentList />} /> */}
        </Routes>
      </div>
    </Router>
  );
}

export default App;
